script:
frappe.ui.form.on('Loan Type', {
	onload: function(frm) {
		$.each(["penalty_income_account", "interest_income_account"], function (i, field) {
			frm.set_query(field, function () {
				return {
					"filters": {
						"company": frm.doc.company,
						"root_type": "Income",
						"is_group": 0
					}
				};
			});
		});

		$.each(["payment_account", "loan_account", "disbursement_account"], function (i, field) {
			frm.set_query(field, function () {
				return {
					"filters": [
                        ["company", "=", frm.doc.company],
                        ["root_type", "IN", ["Asset", "Liability"]],
                        ["is_group", "=", 0]
                    ]
				};
			});
		});
	}
});

frappe.ui.form.on('Employee', {
	employee_category_details(frm) {
		// your code here
		frappe.db.get_doc("Employee Category Details", frm.doc.employee_category_details).then(r => {
		    //alert(r.conge_days);
		    frm.doc.conge_days = r.conge_days;
		    //frm.doc.conge_days_5_years = r.conge_days_5_years;
    		frm.doc.basic_salary_per_day = r.basic_salary_per_day;
    		frm.doc.basic_salary_per_hour = r.basic_salary_per_hour;
    		frm.doc.logement = r.logement;
    		frm.doc.transport = r.transport;
    		frm.doc.conge_days_final_settlement = r.conge_days_final_settlement;
    		frm.doc.conge_year_final_settlement = r.conge_year_final_settlement;
    		frm.dirty();
		    frm.refresh();
		});
	}

});

frappe.ui.form.on('Salary Slip', {
	salary_type(frm) {
		// your code here
		frappe.db.get_value("Salary Structure", {"salary_type": frm.doc.salary_type, "docstatus": 1}, "name", (r) => {
			frm.set_value('salary_structure', r.name);
			frm.events.get_emp_and_working_day_details(frm);
		});
		//frm.doc.salary_structure 
	},
});

frappe.ui.form.on('Loan', {
	currency: function (frm) {
		var company_currency;
		if (!frm.doc.company) {
			company_currency = erpnext.get_currency(frappe.defaults.get_default("Company"));
		} else {
			company_currency = erpnext.get_currency(frm.doc.company);
		}
		if (frm.doc.currency) {
			if (company_currency != frm.doc.currency) {
				frappe.call({
					method: "erpnext.setup.utils.get_exchange_rate",
					args: {
						from_currency: frm.doc.currency,
						to_currency: company_currency,
					},
					callback: function (r) {
						frm.set_value("exchange_rate", flt(r.message));
					}
				});
			} else {
				frm.set_value("exchange_rate", 1.0);
			}
		}
	},
});

frappe.ui.form.on('Employee', {
    refresh: function(frm) {
        if(frm.doc.dependant.length <= 0) frm.get_field('dependant').grid.cannot_add_rows = true;
        else frm.get_field('dependant').grid.cannot_add_rows = false;

        if(frm.doc.dependant.length > 0){
	 	    frm.get_field('dependant').grid.grid_rows[0].docfields[0].read_only = 1;
	 	    frm.get_field('dependant').grid.grid_rows[0].docfields[1].read_only = 1;
	 	    frm.get_field('dependant').grid.grid_rows[0].docfields[2].read_only = 1;
	 	}

        frm.refresh_field('dependant');
    },
	after_save(frm) {
		if(frm.doc.dependant.length <= 0) frm.events.get_employee_dependant(frm);
	},
	get_employee_dependant: function (frm) {
		return frappe.call({
			method: 'paie.override.employee.get_employee_dependant',
			args: { "emp_name": frm.doc.name, },
		    callback: function(r, rt){
    			if (r.message) {
    			    var row = frm.add_child('dependant');
    				row.code = r.message.employee,
				    row.type = 'Employee',
				    row.nom_complet = r.message.employee_name;
				    row.date_naissance = r.message.date_of_birth;
    				frm.refresh_field('dependant');
    				frm.dirty();
    				//frm.save();
    				frm.refresh();
    			}
		    }
		});
	},
});

frappe.ui.form.on('Dependant', {
	before_dependant_remove: function(frm, cdt, cdn) {
	 	var row = locals[cdt][cdn];
	 	if(row.idx != 1) return;
	 	frappe.throw(__("Employee cannot be removed"));
	},
	type: function(frm, cdt, cdn) {
	 	var row = locals[cdt][cdn];
	 		if(row.idx != 1 && row.type == "Employee") {
	 		    frappe.throw(__("Cannot select 'Employee'!!!"));

	 		}
	},
});

frappe.ui.form.on('Leave Application', {
	employee: function(frm) {
		frm.trigger("make_dashboard2");
	},
	from_date: function(frm) {
		frm.trigger("make_dashboard2");
	},
	to_date: function(frm) {
		frm.trigger("make_dashboard2");
	},

	make_dashboard2: function(frm) {
		var leave_details;
		let all_leaves;
		if (frm.doc.employee) {
			frm.set_query('leave_type', function() {
				return {
					filters: [
						['leave_type_name', '!=', 'zzz']
					]
				};
			});
		}
	},
})
